<template>
    <div class="feed-sources-component">
        <h2>RSS Feeds</h2>
        <div class="feed-sources-container">
            <FeedSourceComponent :key="source.reference" v-for="source in feedSources" :source="source" />
        </div>
        <div class="flex gap-small">
            <div>
                <input type="text" placeholder="https://example.com/rss" v-model="newFeedSource">
            </div>
            <div class="flex-auto">
                <button @click="onAddNewFeedSource">Add</button>
            </div>
        </div>
    </div>
</template>

<script lang="ts">
import { computed, defineComponent, ref } from 'vue';

import FeedSourceComponent from '@/components/FeedSource.component.vue';

import { feedService } from '@/service/Feed.service';
import { UseRss } from '@/use/Rss.use';
import { FeedSource } from '@/types/FeedSource.type';

export default defineComponent({
    name: 'FeedSourcesComponent',

    components: {
        FeedSourceComponent,
    },

    setup() {
        const useRss = UseRss();

        const articles = useRss.articles;

        const feedSources = computed<Array<FeedSource> | null>(() => {
            if (articles.value === null)
                return null;

            const sources = [ ...new Set(articles.value.map(x => x.source)) ];

            return sources
                .sort((a, b) => a.title.localeCompare(b.title));
        });

        const newFeedSource = ref<string>('');

        return {
            feedSources,
            newFeedSource,

            async onAddNewFeedSource() {
                const addFeedSourceResult = await feedService.addFeedSource({
                    url: newFeedSource.value,
                });

                if (addFeedSourceResult instanceof Error)
                    return;

                for (const article of addFeedSourceResult) {
                    useRss.articles.value?.push(article);
                }
            },
        }
    },
});
</script>

